+import 'package:drawing/draw/model/stroke.dart';
+import 'package:flutter/material.dart';
+import 'package:hive/hive.dart';
+
+class HomeScreen extends StatefulWidget {
+  const HomeScreen({super.key});
+
+  @override
+  State<HomeScreen> createState() => _HomeScreenState();
+}
+
+class _HomeScreenState extends State<HomeScreen> {
+  late Box<List<Stroke>> _drawingBox;
+
+  @override
+  void initState() {
+    // TODO: implement initState
+    _initializeHive();
+    super.initState();
+  }
+
+  Future<void> _initializeHive() async {
+    _drawingBox = Hive.box<List<Stroke>>('drawingBox');
+  }
+
+  @override
+  Widget build(BuildContext context) {
+    final drawingNames = _drawingBox.keys.cast<String>().toList();
+    return Scaffold(
+      appBar: AppBar(
+        title: const Text('My Drawings'),
+        backgroundColor: Colors.blue,
+        centerTitle: true,
+      ),
+      body: drawingNames.isEmpty
+          ? const Center(
+              child: Text(
+                'No drawings saved yet!',
+                style: TextStyle(fontSize: 24),
+              ),
+            )
+          : GridView.builder(
+              padding: const EdgeInsets.all(8.0),
+              gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
+                crossAxisCount: 2,
+                childAspectRatio: 1.0,
+                crossAxisSpacing: 8.0,
+                mainAxisSpacing: 8.0,
+              ),
+              itemCount: drawingNames.length,
+              itemBuilder: (context, index) {
+                final drawingName = drawingNames[index];
+                return GestureDetector(
+                  onTap: () {
+                    Navigator.pushNamed(context, '/draw',
+                        arguments: {'drawingName': drawingName});
+                  },
+                  child: Card(
+                    child: Center(
+                      child: Text(
+                        drawingName,
+                        style: const TextStyle(fontSize: 18),
+                      ),
+                    ),
+                  ),
+                );
+              },
+            ),
+      floatingActionButton: FloatingActionButton(
+        onPressed: () {
+          Navigator.pushNamed(context, '/draw',
+              arguments: {'drawingName': null, 'drawingData': null});
+        },
+        child: const Icon(Icons.add),
+      ),
+    );
+  }
+}

